"},{"path":"backup server.js","content":"require('dotenv').config();

const express = require('express');
const cors = require('cors');
const { createClient } = require('@supabase/supabase-js');
const axios = require('axios');
const { v4: uuidv4 } = require('uuid');
const moment = require('moment-timezone');

const app = express();
const PORT = process.env.PORT || 3000;

// Initialize Supabase
const supabase = createClient(process.env.SUPABASE_URL, process.env.SUPABASE_SERVICE_ROLE_KEY, {
    auth: { persistSession: false }
});

// 1️⃣ **Request Logging Middleware**: Logs everything (method, URL, headers, body)
app.use((req, res, next) => {
    console.log("\n=== REQUEST LOG START ===");
    console.log(`Incoming ${req.method} request to: ${req.url}`);
    console.log("Headers:", req.headers);
    console.log("Body:", req.body || {});
    console.log("=== REQUEST LOG END ===\n");
    next();
});

// 2️⃣ **Skip Body Parsing for GET Requests**:
app.use((req, res, next) => {
    if (req.method === 'GET') {
        console.log("⚠️ Skipping JSON body parsing for GET request.");
        return next();
    } else {
        return express.json()(req, res, next);
    }
});

// 3️⃣ **Enable CORS**:
app.use(cors());

// ----------------------------------------------------------------------------
// HELPER FUNCTION: sanitizeAndReplaceGreetings
// ----------------------------------------------------------------------------
function sanitizeAndReplaceGreetings(text) {
    if (!text) return "";
    // Remove \r and \n
    let cleaned = text.replace(/[\r\n]+/g, " ");
    return cleaned;
}

// ----------------------------------------------------------------------------
// 📩  CLIENT SEND MESSAGE (POST /client-send-message)
// ----------------------------------------------------------------------------
app.post('/client-send-message', async (req, res) => {
    try {
        console.log("🚀 Received request to /client-send-message", req.body);

        const { recipient_number, message_body, channel, client_guid, customer_name } = req.body;
        if (!recipient_number || !message_body || !channel || !client_guid || !customer_name) {
            console.warn('⚠️ Missing required fields:', req.body);
            return res.status(400).json({ error: 'Missing required fields' });
        }

        const wa_id = uuidv4();
        const tracking_code = uuidv4();
        const timestampUTC = moment.utc().format();

        // Sanitize and replace "hi" / "hello"
        const sanitizedMessage = sanitizeAndReplaceGreetings(message_body);

        console.log(`📩 Sending WhatsApp message to ${recipient_number}: "${sanitizedMessage}"`);

        // Prepare WhatsApp Payload
        const whatsappPayload = {
            payload: {
                name: "inspire_nodejs_chat",
                components: [
                    {
                        type: "header",
                        parameters: [
                            {
                                type: "image",
                                image: { link: "https://saas.botforce.co/curo/curo.png" }
                            }
                        ]
                    },
                    {
                        type: "body",
                        parameters: [
                            { type: "text", text: customer_name },
                            { type: "text", text: sanitizedMessage }
                        ]
                    }
                ],
                language: { code: "en_US", policy: "deterministic" },
                namespace: "eda639f0_956a_448a_8529_7a171538385e"
            },
            phoneNumber: recipient_number
        };

        let wamid = null;
        try {
            // Call WhatsApp API with correct authorization token
            const whatsappResponse = await axios.post(
                "https://api.botforce.co.za/whatsapp-api/v1.0/customer/108412/bot/fa3bacde2da9424c/template",
                whatsappPayload,
                {
                    headers: {
                        Authorization: "Basic 101f541f-ff7b-47b3-80e3-3298630e041c-HFS7ACu",
                        "Content-Type": "application/json"
                    }
                }
            );

            if (whatsappResponse.data.responseObject?.message_id) {
                wamid = whatsappResponse.data.responseObject.message_id;
            }
        } catch (error) {
            console.error("❌ WhatsApp API Error:", error.response?.data || error.message);
            return res.status(500).json({ error: "Failed to send WhatsApp message" });
        }

        // Store message data, including `client_guid`, in Supabase
        await supabase.from('messages_log').upsert([{
            wa_id,
            original_wamid: wamid,
            tracking_code,
            client_guid,
            mobile_number: recipient_number,
            customer_name,
            message: sanitizedMessage,
            customer_response: null,
            channel: "whatsapp",
            status: "sent",
            timestamp: timestampUTC
        }], { onConflict: 'wa_id' });

        res.status(200).json({ success: true, tracking_code, wa_id, wamid });
    } catch (error) {
        console.error("❌ Error in /client-send-message:", error);
        res.status(500).json({ error: 'Internal server error' });
    }
});

// ----------------------------------------------------------------------------
// 📩  BOTFORCE GET LATEST TRACKING (GET /botforce-get-latest-tracking/:recipient_number)
// ----------------------------------------------------------------------------
app.get("/botforce-get-latest-tracking/:recipient_number", async (req, res) => {
    const { recipient_number } = req.params;
    if (!recipient_number) {
        return res.status(400).json({ error: "Missing recipient_number" });
    }

    try {
        console.log(`🔍 GET request for latest tracking for ${recipient_number}`);

        const { data, error } = await supabase
            .from("messages_log")
            .select("tracking_code")
            .eq("mobile_number", recipient_number)
            .order("timestamp", { ascending: false })
            .limit(1)
            .single();

        if (error || !data) {
            console.error("❌ No tracking code found or error:", error);
            return res.status(404).json({ error: "No tracking code found for this number" });
        }

        return res.json({ success: true, tracking_code: data.tracking_code });
    } catch (error) {
        console.error("❌ Error fetching latest tracking code:", error);
        res.status(500).json({ error: "Failed to fetch tracking code" });
    }
});

// ----------------------------------------------------------------------------
// 📩  RECEIVE REPLY (POST /receive-reply)
// ----------------------------------------------------------------------------
app.post("/receive-reply", async (req, res) => {
    const { tracking_code, reply_message } = req.body;

    console.log("=== Incoming /receive-reply Request ===");
    console.log("Request Body:", req.body);

    if (!tracking_code || !reply_message) {
        console.error("❌ Missing tracking_code or reply_message in the request.");
        return res.status(400).json({ error: "Missing tracking_code or reply_message" });
    }

    try {
        console.log("🔍 Fetching client_guid & original_wamid from the database using tracking_code:", tracking_code);

        // Retrieve client_guid and original_wamid from the database using tracking_code
        const { data, error } = await supabase
            .from("messages_log")
            .select("client_guid, original_wamid")
            .eq("tracking_code", tracking_code)
            .single();

        if (error || !data) {
            console.error("❌ No client_guid/original_wamid found for tracking_code:", tracking_code);
            return res.status(404).json({ error: "No client_guid or wamid found for the given tracking_code" });
        }

        const { client_guid, original_wamid } = data;
        console.log("🔍 Found client_guid:", client_guid);
        console.log("🔍 Found original_wamid:", original_wamid);

        // Clean and replace greetings in the reply message
        const cleanedReplyMessage = sanitizeAndReplaceGreetings(reply_message);
        console.log("Cleaned Reply Message:", cleanedReplyMessage);

        // Prepare the payload in the new format required by Inspire
        const timestamp = moment().format('YYYY-MM-DD HH:mm:ss');
        const inspirePayload = {
            ClientGuid: client_guid,
            Timestamp: timestamp,
            Body: cleanedReplyMessage,
            Channel: "whatsapp",
            apiKey: process.env.INSPIRE_API_KEY
        };

        console.log("🔍 Sending data to Inspire...");
        console.log("Inspire Payload:", inspirePayload);

        // Forward the reply to Inspire with authentication in the body
        const inspireResponse = await axios.post(
            "https://inspire-ohs.com/api/V3/WA/GetWaMsg",
            inspirePayload,
            {
                headers: {
                    "Content-Type": "application/json"
                }
            }
        );

        console.log("🔍 Inspire Response:", inspireResponse.data);

        // Return the success response with a dynamic wamid from your DB
        // (the 'original_wamid' from the original message)
        res.json({
            success: true,
            tracking_code: tracking_code,
            wa_id: uuidv4(),  // Optionally generate a new wa_id if needed
            wamid: original_wamid
        });
    } catch (error) {
        console.error("❌ Error in /receive-reply logic:", error);
        res.status(500).json({ error: "Failed to store reply or send it to Inspire" });
    }
});

// =====================================
// Debug Route: /receive-reply-debug
// =====================================
app.post('/receive-reply-debug', (req, res) => {
  let rawData = '';

  req.on('data', (chunk) => {
    rawData += chunk;
  });

  req.on('end', () => {
    console.log("\n=== RAW PAYLOAD START ===");
    console.log(rawData); // Logs exactly what arrived
    console.log("=== RAW PAYLOAD END ===\n");

    try {
      const parsed = JSON.parse(rawData);
      console.log("Parsed JSON:", parsed);
      res.json({ success: true, parsed });
    } catch (err) {
      console.error("Error parsing JSON:", err);
      res.status(400).json({ error: "Invalid JSON body", details: err.message });
    }
  });
});

// ----------------------------------------------------------------------------
// Start Server
// ----------------------------------------------------------------------------
app.listen(PORT, () => {
    console.log(`\n✅ Server running on port ${PORT}`);
});
"},{"path":"billing.js","content":"const express = require('express');
const router = express.Router();
const moment = require('moment-timezone');
const { createClient } = require('@supabase/supabase-js');
const path = require('path');
const XLSX = require('xlsx');
const cron = require('node-cron');
const fs = require('fs');

// Initialize Supabase with service role key
const supabase = createClient(
  process.env.SUPABASE_URL,
  process.env.SUPABASE_SERVICE_ROLE_KEY,
  {
    auth: { persistSession: false }
  }
);

// Create reports directory if it doesn't exist
const reportsDir = path.join(__dirname, 'reports');
if (!fs.existsSync(reportsDir)) {
  fs.mkdirSync(reportsDir, { recursive: true });
}

// Automated monthly export function
async function generateAndStoreMonthlyReport() {
  try {
    console.log('🔄 Starting automated monthly billing export...');

    const now = moment().tz('Africa/Johannesburg');
    const startOfMonth = now.clone().startOf('month');
    const monthYear = startOfMonth.format('YYYY-MM');

    // Get all billing records for current month (same logic as export endpoint)
    let allBillingRecords = [];
    let page = 0;
    const pageSize = 1000;

    while (true) {
      const { data: pageRecords, error: pageError } = await supabase
        .from('billing_records')
        .select('*')
        .gte('message_timestamp', startOfMonth.format())
        .order('message_timestamp', { ascending: false })
        .range(page * pageSize, (page + 1) * pageSize - 1);

      if (pageError) throw pageError;
      if (!pageRecords || pageRecords.length === 0) break;

      allBillingRecords = [...allBillingRecords, ...pageRecords];
      if (pageRecords.length < pageSize) break;
      page++;
    }

    const billingRecords = allBillingRecords;

    if (!billingRecords || billingRecords.length === 0) {
      console.log('📧 No billing data for this month - creating empty report');
    }

    // Generate Excel file (same logic as export endpoint)
    const sessions = billingRecords.reduce((acc, record) => {
      const mobileNumber = record.mobile_number || record.recipient_number;
      if (!acc[mobileNumber]) {
        acc[mobileNumber] = [];
      }
      acc[mobileNumber].push({
        timestamp: moment(record.message_timestamp),
        sessionStart: moment(record.session_start_time),
        costs: {
          utility: parseFloat(record.cost_utility) || 0,
          carrier: parseFloat(record.cost_carrier) || 0,
          mau: parseFloat(record.cost_mau) || 0,
          total: parseFloat(record.total_cost) || 0
        },
        is_mau_charged: record.is_mau_charged
      });
      return acc;
    }, {});

    let sessionCount = 0;
    let carrierTotal = 0;
    let utilityTotal = 0;

    for (const [user, messages] of Object.entries(sessions)) {
      if (!messages || messages.length === 0) continue;

      messages.sort((a, b) => a.timestamp - b.timestamp);
      let activeSessions = [];
      let currentSession = [messages[0]];

      for (let i = 1; i < messages.length; i++) {
        const timeDiff = messages[i].timestamp.diff(currentSession[0].timestamp, 'minutes');
        if (timeDiff <= 1430) {
          currentSession.push(messages[i]);
        } else {
          activeSessions.push(currentSession);
          currentSession = [messages[i]];
        }
      }
      if (currentSession.length > 0) {
        activeSessions.push(currentSession);
      }

      sessionCount += activeSessions.length;
      carrierTotal += activeSessions.length * 0.01;
      utilityTotal += activeSessions.length * 0.0076;
    }

    const sessionCost = carrierTotal + utilityTotal;
    const mauCost = billingRecords
      .filter(record => record.is_mau_charged === true)
      .reduce((sum, record) => sum + (parseFloat(record.cost_mau) || 0), 0);
    const totalCost = sessionCost + mauCost;
    const monthlyActiveUsers = Object.keys(sessions).length;

    // Create line items data
    const lineItemsData = billingRecords.map(record => ({
      'Timestamp': moment(record.message_timestamp).tz('Africa/Johannesburg').format('DD/MM/YYYY, HH:mm:ss'),
      'Mobile Number': record.mobile_number,
      'WhatsApp ID': record.whatsapp_message_id,
      'Utility Cost': `$${(parseFloat(record.cost_utility) || 0).toFixed(4)}`,
      'Carrier Cost': `$${(parseFloat(record.cost_carrier) || 0).toFixed(4)}`,
      'MAU Cost': `$${(parseFloat(record.cost_mau) || 0).toFixed(4)}`,
      'Total Cost': `$${(parseFloat(record.total_cost) || 0).toFixed(4)}`,
      'Session Start': moment(record.session_start_time).tz('Africa/Johannesburg').format('DD/MM/YYYY, HH:mm:ss'),
      'Is MAU Charged': record.is_mau_charged ? 'Yes' : 'No'
    }));

    // Create summary data
    const summaryStatsData = [
      {
        'Metric': 'Billing Period',
        'Value': now.format('MMMM YYYY'),
        'Cost (USD)': '',
        'Details': `${startOfMonth.format('YYYY-MM-DD')} to ${now.format('YYYY-MM-DD')}`
      },
      {
        'Metric': 'Export Date',
        'Value': now.format('YYYY-MM-DD HH:mm:ss'),
        'Cost (USD)': '',
        'Details': 'Africa/Johannesburg timezone'
      },
      {
        'Metric': '',
        'Value': '',
        'Cost (USD)': '',
        'Details': ''
      },
      {
        'Metric': 'Total Messages',
        'Value': billingRecords.length,
        'Cost (USD)': '',
        'Details': 'All messages processed this month'
      },
      {
        'Metric': 'Billable Sessions',
        'Value': sessionCount,
        'Cost (USD)': sessionCost.toFixed(4),
        'Details': 'Sessions with 23h50m gap threshold'
      },
      {
        'Metric': 'Monthly Active Users',
        'Value': monthlyActiveUsers,
        'Cost (USD)': mauCost.toFixed(4),
        'Details': 'Unique mobile numbers'
      },
      {
        'Metric': 'TOTAL COST',
        'Value': '',
        'Cost (USD)': totalCost.toFixed(4),
        'Details': 'Session costs + MAU costs'
      }
    ];

    // Create Excel workbook
    const workbook = XLSX.utils.book_new();

    // Line Items sheet
    const lineItemsWorksheet = XLSX.utils.json_to_sheet(lineItemsData);
    lineItemsWorksheet['!cols'] = [
      { width: 18 }, { width: 15 }, { width: 35 }, { width: 12 },
      { width: 12 }, { width: 12 }, { width: 12 }, { width: 18 }, { width: 12 }
    ];
    XLSX.utils.book_append_sheet(workbook, lineItemsWorksheet, 'Line Items');

    // Summary sheet
    const summaryWorksheet = XLSX.utils.json_to_sheet(summaryStatsData);
    summaryWorksheet['!cols'] = [
      { width: 20 }, { width: 15 }, { width: 15 }, { width: 35 }
    ];
    XLSX.utils.book_append_sheet(workbook, summaryWorksheet, 'Summary');

    // Generate buffer
    const buffer = XLSX.write(workbook, { type: 'buffer', bookType: 'xlsx' });

    // Define the file path
    const filePath = path.join(reportsDir, `billing-report-${monthYear}.xlsx`);

    // Write the buffer to the file
    fs.writeFileSync(filePath, buffer);

    console.log(`✅ Monthly billing report stored successfully for ${monthYear} at ${filePath}`);

  } catch (error) {
    console.error('❌ Error generating/storing monthly report:', error);
  }
}

// Schedule automated export for last day of month at 23:50
// Cron: minute hour day month day-of-week
// This runs at 23:50 on the last day of every month
cron.schedule('50 23 28-31 * *', () => {
  const now = moment().tz('Africa/Johannesburg');
  const tomorrow = now.clone().add(1, 'day');

  // Only run if tomorrow is the first day of next month (i.e., today is last day of month)
  if (tomorrow.date() === 1) {
    console.log('🔄 Running scheduled monthly billing export...');
    generateAndStoreMonthlyReport();
  }
}, {
  scheduled: true,
  timezone: "Africa/Johannesburg"
});

console.log('📅 Monthly billing export scheduled for last day of month at 23:50 (Africa/Johannesburg)');

// Auth middleware
const checkAuth = (req, res, next) => {
  res.setHeader('Content-Type', 'application/json');
  return next();
};

router.get('/', (req, res) => {
  res.sendFile(path.join(__dirname, 'public/billing.html'));
});

router.get('/exchange-rate', checkAuth, async (req, res) => {
  try {
    const axios = require('axios');
    const response = await axios.get('https://open.er-api.com/v6/latest/USD');
    if (!response.data?.rates?.ZAR) {
      throw new Error('Invalid exchange rate response');
    }
    console.log('Exchange rate fetched:', response.data.rates.ZAR);
    res.json({ rate: response.data.rates.ZAR });
  } catch (error) {
    console.error('Exchange rate error:', error);
    res.json({ rate: 20.00 }); // Fallback rate if API fails
  }
});

router.get('/recent-sessions', checkAuth, async (req, res) => {
  try {
    const { data: recentSessions, error } = await supabase
      .from('billing_records')
      .select('message_timestamp, mobile_number, whatsapp_message_id, cost_utility, cost_carrier, cost_mau, total_cost, session_start_time')
      .order('message_timestamp', { ascending: false })
      .limit(10);

    if (error) throw error;
    res.json({ sessions: recentSessions });
  } catch (error) {
    console.error('Error fetching recent sessions:', error);
    res.status(500).json({ error: 'Failed to fetch recent sessions' });
  }
});

router.get('/stats', checkAuth, async (req, res) => {
  try {
    const now = moment().tz('Africa/Johannesburg');
    const startOfMonth = now.clone().startOf('month');

    // Get total count first
    const { count: totalCount, error: countError } = await supabase
      .from('billing_records')
      .select('*', { count: 'exact', head: true })
      .gte('message_timestamp', startOfMonth.format());

    if (countError) throw countError;

    // Get all billing records for current month using pagination
    let allBillingRecords = [];
    let page = 0;
    const pageSize = 1000;

    while (true) {
      const { data: pageRecords, error: pageError } = await supabase
        .from('billing_records')
        .select('*')
        .gte('message_timestamp', startOfMonth.format())
        .order('message_timestamp', { ascending: false })
        .range(page * pageSize, (page + 1) * pageSize - 1);

      if (pageError) throw pageError;
      if (!pageRecords || pageRecords.length === 0) break;

      allBillingRecords = [...allBillingRecords, ...pageRecords];
      if (pageRecords.length < pageSize) break;
      page++;
    }

    console.log('Billing query params:', {
      startTime: startOfMonth.format(),
      totalCount,
      recordsFetched: allBillingRecords.length
    });

    const billingRecords = allBillingRecords;

    if (!billingRecords || billingRecords.length === 0) {
      console.error('Supabase query error:', queryError);
      console.error('Query params:', {
        startOfMonth: startOfMonth.format(),
        timezone: now.tz()
      });
      throw queryError;
    }

    // console.log('Billing records fetched:', billingRecords?.length || 0);

    if (!billingRecords || billingRecords.length === 0) {
      return res.json({
        totalMessages: 0,
        billableSessions: 0,
        monthlyActiveUsers: 0,
        sessionCost: 0,
        mauCost: 0,
        totalCost: 0,
        startDate: startOfMonth.format(),
        endDate: now.format()
      });
    }

    // Initialize metrics
    const totalMessages = billingRecords.length;
    let billableSessions = 0;
    let monthlyActiveUsers = 0;
    let sessionCost = 0;
    let mauCost = 0;
    let totalCost = 0;

    // Group messages by user and session
    console.log('Processing records for unique users...');
    const sessions = billingRecords.reduce((acc, record) => {
      const mobileNumber = record.mobile_number || record.recipient_number;
      if (!acc[mobileNumber]) {
        acc[mobileNumber] = [];
      }
      acc[mobileNumber].push({
        timestamp: moment(record.message_timestamp),
        sessionStart: moment(record.session_start_time),
        costs: {
          utility: parseFloat(record.cost_utility) || 0,
          carrier: parseFloat(record.cost_carrier) || 0,
          mau: parseFloat(record.cost_mau) || 0,
          total: parseFloat(record.total_cost) || 0
        },
        is_mau_charged: record.is_mau_charged
      });
      // console.log(`Added record for ${mobileNumber}, session start: ${record.session_start_time}`);
      return acc;
    }, {});

    // Initialize metrics
    let carrierCount = 0;
    let carrierTotal = 0;
    let utilityCount = 0;
    let utilityTotal = 0;
    let sessionCount = 0;

    // Calculate metrics from actual database records
    for (const [user, messages] of Object.entries(sessions)) {
      if (!messages || messages.length === 0) continue;

      // Sort messages by timestamp
      messages.sort((a, b) => a.timestamp - b.timestamp);

      // Group into sessions with 23h50m window
      let activeSessions = [];
      let currentSession = [messages[0]];

      // Group messages into sessions
      for (let i = 1; i < messages.length; i++) {
        const timeDiff = messages[i].timestamp.diff(currentSession[0].timestamp, 'minutes');
        if (timeDiff <= 1430) { // 23 hours and 50 minutes
          currentSession.push(messages[i]);
        } else {
          activeSessions.push(currentSession);
          currentSession = [messages[i]];
        }
      }
      if (currentSession.length > 0) {
        activeSessions.push(currentSession);
      }

      // Only charge once per actual session
      sessionCount += activeSessions.length;
      carrierCount += activeSessions.length;
      utilityCount += activeSessions.length;

      // Calculate costs per unique session
      carrierTotal += activeSessions.length * 0.01; // $0.01 once per session
      utilityTotal += activeSessions.length * 0.0076; // $0.0076 once per session

      billableSessions += activeSessions.length;
    }

    // Calculate session cost as sum of carrier and utility fees per session
    sessionCost = carrierTotal + utilityTotal;

    // Calculate MAU cost from actual database records (not recalculated)
    mauCost = billingRecords
      .filter(record => record.is_mau_charged === true)
      .reduce((sum, record) => sum + (parseFloat(record.cost_mau) || 0), 0);

    // Count unique mobile numbers for MAU
    monthlyActiveUsers = Object.keys(sessions).length;

    // Calculate total cost
    totalCost = sessionCost + mauCost;

    res.json({
      totalMessages,
      billableSessions,
      monthlyActiveUsers,
      sessionCost,
      mauCost,
      totalCost,
      carrierCount,
      carrierTotal,
      utilityCount,
      utilityTotal,
      startDate: startOfMonth.format(),
      endDate: now.format()
    });
  } catch (error) {
    console.error('Error in /stats route:', error);
    res.status(500).json({ error: 'Failed to fetch stats' });
  }
});

