  <meta name="pinterest" content="noindex, nofollow, noarchive, nosnippet, noimageindex, nocache" />
  <meta name="whatsapp" content="noindex, nofollow, noarchive, nosnippet, noimageindex, nocache" />
  <meta name="ccbot" content="noindex, nofollow, noarchive, nosnippet, noimageindex, nocache" />
  <meta name="chatgpt-user" content="noindex, nofollow, noarchive, nosnippet, noimageindex, nocache" />
  <meta name="gptbot" content="noindex, nofollow, noarchive, nosnippet, noimageindex, nocache" />
  <meta name="claude-web" content="noindex, nofollow, noarchive, nosnippet, noimageindex, nocache" />
  <meta name="claudebot" content="noindex, nofollow, noarchive, nosnippet, noimageindex, nocache" />
  <meta name="google-extended" content="noindex, nofollow, noarchive, nosnippet, noimageindex, nocache" />
  <meta name="gemini" content="noindex, nofollow, noarchive, nosnippet, noimageindex, nocache" />
  <meta name="bard" content="noindex, nofollow, noarchive, nosnippet, noimageindex, nocache" />
  <title>Curo Inspire Message Tracking</title>
  <link rel="stylesheet" href="/styles/main.css">
  <style>
    :root {
      --primary-gradient: linear-gradient(135deg, #6366f1, #8b5cf6);
      --primary-color: #4f46e5;
      --primary-hover: #4338ca;
      --bg-color: #f3f4f6;
      --text-color: #1f2937;
      --text-light: #6b7280;
      --border-color: #e5e7eb;
      --card-bg: white;
    }

    body {
      min-height: 100vh;
      display: flex;
      flex-direction: column;
      align-items: center;
      justify-content: center;
      padding: 1rem 0;
      background-color: var(--bg-color);
      font-family: 'Inter', -apple-system, BlinkMacSystemFont, sans-serif;
    }

    .container {
      width: 100%;
      max-width: 64rem;
      padding: 1rem;
      margin: 0 auto;
    }

    @media (min-width: 640px) {
      body {
        padding: 2.5rem 0;
      }
      .container {
        padding: 2rem;
      }
    }

    .title {
      font-size: 1.5rem;
      font-weight: bold;
      color: var(--text-color);
      text-align: center;
      margin-bottom: 1.5rem;
    }

    @media (min-width: 640px) {
      .title {
        font-size: 1.875rem;
      }
    }

    .subtitle {
      font-size: 0.875rem;
      color: var(--text-light);
      text-align: center;
      margin-bottom: 1rem;
    }

    .search-form {
      display: flex;
      flex-direction: column;
      gap: 0.5rem;
      margin-bottom: 1rem;
    }

    @media (min-width: 640px) {
      .search-form {
        flex-direction: row;
      }
    }

    .search-input {
      width: 100%;
      padding: 0.75rem;
      border: 1px solid var(--border-color);
      border-radius: 0.5rem;
      font-size: 1rem;
    }

    .search-input:focus {
      outline: none;
      border-color: var(--primary-color);
      box-shadow: 0 0 0 2px rgba(79, 70, 229, 0.2);
    }

    .search-button {
      padding: 0.75rem 1.5rem;
      background-color: var(--primary-color);
      color: white;
      border: none;
      border-radius: 0.5rem;
      cursor: pointer;
      transition: background-color 0.2s;
    }

    .search-button:hover {
      background-color: var(--primary-hover);
    }

    #loading {
      display: none;
      text-align: center;
      color: var(--primary-color);
      font-weight: 500;
      margin-top: 1rem;
    }

    #message-status {
      margin-top: 1.5rem;
    }

    .status-card {
      background: var(--card-bg);
      border-radius: 0.75rem;
      box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
      padding: 1.5rem;
      margin-bottom: 1.5rem;
      transition: all 0.3s ease;
    }

    .status-card:hover {
      transform: translateY(-2px);
      box-shadow: 0 8px 12px rgba(0, 0, 0, 0.15);
    }

    .status-header {
      display: flex;
      justify-content: space-between;
      align-items: center;
      margin-bottom: 1rem;
      padding-bottom: 0.75rem;
      border-bottom: 1px solid var(--border-color);
    }

    .message-id {
      font-size: 0.875rem;
      color: var(--text-light);
      word-break: break-all;
    }

    .recipient {
      font-size: 1rem;
      font-weight: 500;
      margin: 0.5rem 0;
    }

    .timestamp {
      font-size: 0.875rem;
      color: var(--text-light);
    }

    .status-flow {
      display: flex;
      align-items: center;
      flex-wrap: wrap;
      gap: 0.5rem;
      margin-top: 1rem;
    }

    .status-badge {
      padding: 0.375rem 0.75rem;
      border-radius: 0.5rem;
      font-size: 0.875rem;
      font-weight: 500;
    }

    .highlight-status {
      background: var(--primary-gradient);
      color: white;
    }

    .status-default {
      background-color: var(--border-color);
      color: var(--text-light);
    }

    .status-arrow {
      color: var(--text-light);
      margin: 0 0.25rem;
    }

    details[open] summary ~ * {
      animation: fadeIn 0.5s ease-in-out;
    }

    @keyframes fadeIn {
      from { opacity: 0; }
      to { opacity: 1; }
    }
  </style>
</head>
<body>
  <div class="container">
    <h2 class="title">Curo Inspire Message Tracking</h2>
    <p class="subtitle">Showing messages from the last 30 days</p>

    <form class="search-form" onsubmit="event.preventDefault(); searchStatus();">
      <input
        id="phone-number"
        type="text"
        placeholder="Enter phone number..."
        class="search-input"
      />
      <button type="submit" class="search-button">
        Search
      </button>
    </form>

    <div id="loading">Loading...</div>
    <div id="message-status"></div>
  </div>

  <script>
    function parseAsUTC(dbTimestamp) {
      if (!dbTimestamp) return null;
      if (dbTimestamp.includes('T') && (dbTimestamp.endsWith('Z') || dbTimestamp.includes('+'))) {
        return new Date(dbTimestamp);
      }
      return new Date(dbTimestamp.trim().replace(' ', 'T') + 'Z');
    }

    const SASTFormatter = new Intl.DateTimeFormat("en-ZA", {
      timeZone: "Africa/Johannesburg",
      year: "numeric",
      month: "2-digit",
      day: "2-digit",
      hour: "2-digit",
      minute: "2-digit",
      second: "2-digit",
      hour12: false
    });
    const SASTDayFormatter = new Intl.DateTimeFormat("en-ZA", {
      timeZone: "Africa/Johannesburg",
      year: "numeric",
      month: "2-digit",
      day: "2-digit"
    });

    async function searchStatus() {
      const phoneNumber = document.getElementById('phone-number').value.trim();
      if (!phoneNumber) {
        alert('Please enter a phone number');
        return;
      }

      const resultsDiv = document.getElementById('message-status');
      const loadingDiv = document.getElementById('loading');
      resultsDiv.innerHTML = '';
      loadingDiv.classList.remove('hidden');

      try {
        const response = await fetch(`/get-message-status?phone_number=${encodeURIComponent(phoneNumber)}`);
        const data = await response.json();
        loadingDiv.classList.add('hidden');

        if (!data.messages || data.messages.length === 0) {
          resultsDiv.innerHTML = '<p class="text-red-500 text-center">No messages found in the last 30 days.</p>';
          return;
        }

        data.messages.sort((a, b) => parseAsUTC(b.timestamp) - parseAsUTC(a.timestamp));

        const grouped = {};
        data.messages.forEach(msg => {
          const day = SASTDayFormatter.format(parseAsUTC(msg.timestamp));
          if (!grouped[day]) grouped[day] = [];
          grouped[day].push(msg);
        });

        for (const day in grouped) {
          let dayHTML = `
            <details class="mb-6" ${Object.keys(grouped)[0] === day ? 'open' : ''}>
              <summary class="text-lg font-semibold mb-4 cursor-pointer hover:text-primary-600">${day}</summary>
              <div class="space-y-4">
                ${grouped[day].map(msg => createMessageCard(msg)).join('')}
              </div>
            </details>
          `;
          resultsDiv.innerHTML += dayHTML;
        }
      } catch (e) {
        loadingDiv.classList.add('hidden');
        resultsDiv.innerHTML = '<p class="text-red-500 text-center">Error fetching results.</p>';
      }
    }

    function renderStatus(current) {
      const statuses = ['sent', 'delivered', 'read', 'failed'];

      return statuses.map(status => {
        let shouldHighlight = false;

        if (current === 'failed') {
          shouldHighlight = status === 'failed';
        } else if (current === 'read') {
          shouldHighlight = status === 'read';
        } else if (current === 'delivered') {
          shouldHighlight = status === 'delivered';
        } else if (current === 'sent') {
          shouldHighlight = status === 'sent';
        }

        return `
          <span class="status-badge ${shouldHighlight ? 'highlight-status' : 'status-default'}">
            ${status.toUpperCase()}
          </span>
          ${status !== 'failed' ? '<span class="status-arrow">→</span>' : ''}
        `;
      }).join('');
    }

    function createMessageCard(msg) {
      const time = SASTFormatter.format(parseAsUTC(msg.timestamp));
      return `
        <div class="status-card">
          <div class="status-header">
            <div class="message-id">ID: ${msg.original_wamid || 'N/A'}</div>
          </div>
          <div class="recipient">Recipient: ${msg.mobile_number}</div>
          <div class="timestamp">Timestamp: ${time}</div>
          <div class="status-flow">
            ${renderStatus(msg.status)}
          </div>
          ${msg.error_code || msg.error_message ? `
          <div class="mt-3 p-3 bg-red-50 rounded-lg">
            ${msg.error_code ? `<div class="text-sm text-red-600">Error Code: ${msg.error_code}</div>` : ''}
            ${msg.error_message ? `<div class="text-sm text-red-600">Error Message: ${msg.error_message}</div>` : ''}
          </div>` : ''}
        </div>`;
    }
  </script>
</body>
</html>"},{"path":"public/robots.txt","content":"
