;\n      }\n    });\n\n    // Check for numbers with no MAU charges\n    const numbersWithMessages = [...new Set(dbRecords.map(r => r.mobile_number))];\n    const numbersWithMAUCharges = [...new Set(dbRecords.filter(r => r.is_mau_charged).map(r => r.mobile_number))];\n\n    numbersWithMessages.forEach(number => {\n      if (!numbersWithMAUCharges.includes(number)) {\n        sampleIssues.push({\n          type: 'Missing MAU charge',\n          number,\n          issue: 'Has messages but no MAU charge'\n        });\n      }\n    });\n\n    res.json({\n      timestamp: now.format(),\n      month: startOfMonth.format('YYYY-MM'),\n      isValid: issues.length === 0,\n      summary: `${issues.length} validation issues found`,\n\n      databaseFacts: dbFacts,\n      calculatedStats: calculatedStats,\n      validationResults: validationResults,\n\n      issues: issues,\n      sampleIssues: sampleIssues.slice(0, 10), // Show first 10 sample issues\n\n      recommendations: issues.length > 0 ? [\n        'Check webhook logic for MAU charging',\n        'Verify session grouping is working correctly',\n        'Run database cleanup if needed'\n      ] : ['Billing system is working correctly']\n    });\n\n  } catch (error) {\n    console.error('Error in billing validation:', error);\n    res.status(500).json({ error: 'Failed to validate billing data' });\n  }\n});\n\n// Removed test email endpoint\n\n// Excel export endpoint\nrouter.get('/export-excel', checkAuth, async (req, res) => {\n  try {\n    const now = moment().tz('Africa/Johannesburg');\n    const startOfMonth = now.clone().startOf('month');\n    const monthYear = startOfMonth.format('YYYY-MM');\n\n    // Get all billing records for current month\n    let allBillingRecords = [];\n    let page = 0;\n    const pageSize = 1000;\n\n    while (true) {\n      const { data: pageRecords, error: pageError } = await supabase\n        .from('billing_records')\n        .select('*')\n        .gte('message_timestamp', startOfMonth.format())\n        .order('message_timestamp', { ascending: false })\n        .range(page * pageSize, (page + 1) * pageSize - 1);\n\n      if (pageError) throw pageError;\n      if (!pageRecords || pageRecords.length === 0) break;\n\n      allBillingRecords = [...allBillingRecords, ...pageRecords];\n      if (pageRecords.length < pageSize) break;\n      page++;\n    }\n\n    const billingRecords = allBillingRecords;\n\n    // Calculate the same stats as the dashboard\n    if (!billingRecords || billingRecords.length === 0) {\n      const emptyData = [{\n        'Metric': 'No data available for this month',\n        'Value': '',\n        'Cost (USD)': '',\n        'Details': ''\n      }];\n\n      const worksheet = XLSX.utils.json_to_sheet(emptyData);\n      const workbook = XLSX.utils.book_new();\n      XLSX.utils.book_append_sheet(workbook, worksheet, 'Billing Stats');\n\n      const buffer = XLSX.write(workbook, { type: 'buffer', bookType: 'xlsx' });\n\n      res.setHeader('Content-Type', 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet');\n      res.setHeader('Content-Disposition', `attachment; filename=\"billing-stats-${monthYear}.xlsx\"`);\n      return res.send(buffer);\n    }\n\n    // Group messages by user and session (same logic as stats endpoint)\n    const sessions = billingRecords.reduce((acc, record) => {\n      const mobileNumber = record.mobile_number || record.recipient_number;\n      if (!acc[mobileNumber]) {\n        acc[mobileNumber] = [];\n      }\n      acc[mobileNumber].push({\n        timestamp: moment(record.message_timestamp),\n        sessionStart: moment(record.session_start_time),\n        costs: {\n          utility: parseFloat(record.cost_utility) || 0,\n          carrier: parseFloat(record.cost_carrier) || 0,\n          mau: parseFloat(record.cost_mau) || 0,\n          total: parseFloat(record.total_cost) || 0\n        },\n        is_mau_charged: record.is_mau_charged\n      });\n      return acc;\n    }, {});\n\n    let carrierCount = 0;\n    let carrierTotal = 0;\n    let utilityCount = 0;\n    let utilityTotal = 0;\n    let sessionCount = 0;\n\n    // Calculate metrics\n    for (const [user, messages] of Object.entries(sessions)) {\n      if (!messages || messages.length === 0) continue;\n\n      messages.sort((a, b) => a.timestamp - b.timestamp);\n\n      let activeSessions = [];\n      let currentSession = [messages[0]];\n\n      for (let i = 1; i < messages.length; i++) {\n        const timeDiff = messages[i].timestamp.diff(currentSession[0].timestamp, 'minutes');\n        if (timeDiff <= 1430) {\n          currentSession.push(messages[i]);\n        } else {\n          activeSessions.push(currentSession);\n          currentSession = [messages[i]];\n        }\n      }\n      if (currentSession.length > 0) {\n        activeSessions.push(currentSession);\n      }\n\n      sessionCount += activeSessions.length;\n      carrierCount += activeSessions.length;\n      utilityCount += activeSessions.length;\n      carrierTotal += activeSessions.length * 0.01;\n      utilityTotal += activeSessions.length * 0.0076;\n    }\n\n    const sessionCost = carrierTotal + utilityTotal;\n    const mauCost = billingRecords\n      .filter(record => record.is_mau_charged === true)\n      .reduce((sum, record) => sum + (parseFloat(record.cost_mau) || 0), 0);\n    const totalCost = sessionCost + mauCost;\n    const monthlyActiveUsers = Object.keys(sessions).length;\n\n    // Create detailed line items data\n    const lineItemsData = billingRecords.map(record => ({\n      'Timestamp': moment(record.message_timestamp).tz('Africa/Johannesburg').format('DD/MM/YYYY, HH:mm:ss'),\n      'Mobile Number': record.mobile_number,\n      'WhatsApp ID': record.whatsapp_message_id,\n      'Utility Cost': `$${(parseFloat(record.cost_utility) || 0).toFixed(4)}`,\n      'Carrier Cost': `$${(parseFloat(record.cost_carrier) || 0).toFixed(4)}`,\n      'MAU Cost': `$${(parseFloat(record.cost_mau) || 0).toFixed(4)}`,\n      'Total Cost': `$${(parseFloat(record.total_cost) || 0).toFixed(4)}`,\n      'Session Start': moment(record.session_start_time).tz('Africa/Johannesburg').format('DD/MM/YYYY, HH:mm:ss'),\n      'Is MAU Charged': record.is_mau_charged ? 'Yes' : 'No'\n    }));\n\n    // Create summary stats data (for optional summary sheet)\n    const summaryStatsData = [\n      {\n        'Metric': 'Billing Period',\n        'Value': now.format('MMMM YYYY'),\n        'Cost (USD)': '',\n        'Details': `${startOfMonth.format('YYYY-MM-DD')} to ${now.format('YYYY-MM-DD')}`\n      },\n      {\n        'Metric': 'Export Date',\n        'Value': now.format('YYYY-MM-DD HH:mm:ss'),\n        'Cost (USD)': '',\n        'Details': 'Africa/Johannesburg timezone'\n      },\n      {\n        'Metric': '',\n        'Value': '',\n        'Cost (USD)': '',\n        'Details': ''\n      },\n      {\n        'Metric': 'Total Messages',\n        'Value': billingRecords.length,\n        'Cost (USD)': '',\n        'Details': 'All messages processed this month'\n      },\n      {\n        'Metric': 'Billable Sessions',\n        'Value': sessionCount,\n        'Cost (USD)': sessionCost.toFixed(4),\n        'Details': 'Sessions with 23hh50mm gap threshold'\n      },\n      {\n        'Metric': 'Monthly Active Users',\n        'Value': monthlyActiveUsers,\n        'Cost (USD)': mauCost.toFixed(4),\n        'Details': 'Unique mobile numbers'\n      },\n      {\n        'Metric': 'TOTAL COST',\n        'Value': '',\n        'Cost (USD)': totalCost.toFixed(4),\n        'Details': 'Session costs + MAU costs'\n      }\n    ];\n\n    // Create Excel workbook with line items as primary sheet\n    const workbook = XLSX.utils.book_new();\n\n    // Line Items sheet (main sheet)\n    const lineItemsWorksheet = XLSX.utils.json_to_sheet(lineItemsData);\n    lineItemsWorksheet['!cols'] = [\n      { width: 18 }, // Timestamp\n      { width: 15 }, // Mobile Number\n      { width: 35 }, // WhatsApp ID\n      { width: 12 }, // Utility Cost\n      { width: 12 }, // Carrier Cost\n      { width: 12 }, // MAU Cost\n      { width: 12 }, // Total Cost\n      { width: 18 }, // Session Start\n      { width: 12 }  // Is MAU Charged\n    ];\n    XLSX.utils.book_append_sheet(workbook, lineItemsWorksheet, 'Line Items');\n\n    // Summary sheet (optional)\n    const summaryWorksheet = XLSX.utils.json_to_sheet(summaryStatsData);\n    summaryWorksheet['!cols'] = [\n      { width: 20 }, // Metric\n      { width: 15 }, // Value\n      { width: 15 }, // Cost\n      { width: 35 }  // Details\n    ];\n    XLSX.utils.book_append_sheet(workbook, summaryWorksheet, 'Summary');\n\n    // Generate buffer\n    const buffer = XLSX.write(workbook, { type: 'buffer', bookType: 'xlsx' });\n\n    // Set response headers\n    res.setHeader('Content-Type', 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet');\n\n    const filename = `billing-stats-${monthYear}.xlsx`;\n    res.setHeader('Content-Disposition', `attachment; filename=\"${filename}\"`);\n\n    // Define the file path\n    const filePath = path.join(reportsDir, filename);\n\n    // Write the buffer to the file\n    fs.writeFileSync(filePath, buffer);\n    res.send(buffer);\n\n  } catch (error) {\n    console.error('Error generating Excel export:', error);\n    res.status(500).json({ error: 'Failed to generate Excel export' });\n  }\n});\n\n// Route to download the report for a specific month\nrouter.get('/download-report', checkAuth, (req, res) => {\n  const { month } = req.query;\n\n  if (!month) {\n    return res.status(400).send('Month parameter is required');\n  }\n\n  // Correct filename\n  const filename = `billing-stats-${month}.xlsx`;\n  const filePath = path.join(reportsDir, filename);\n\n  // Check if the file exists\n  if (!fs.existsSync(filePath)) {\n    console.log(`File not found: ${filePath}`);\n    return res.status(404).send('Report not found for the specified month');\n  }\n\n  // Set appropriate headers for file download\n  res.setHeader('Content-Type', 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet');\n  res.setHeader('Content-Disposition', `attachment; filename=\"${filename}\"`);\n\n  // Stream the file to the response\n  const fileStream = fs.createReadStream(filePath);\n  fileStream.pipe(res);\n\n  fileStream.on('error', (err) => {\n    console.error('File stream error:', err);\n    res.status(500).send('Error streaming the report');\n  });\n});\n\n// Serve static files from the \"reports\" directory\nrouter.use('/reports', express.static(path.join(__dirname, 'reports')));\n\n// Route to list available reports\nrouter.get('/list-reports', checkAuth, (req, res) => {\n  fs.readdir(reportsDir, (err, files) => {\n    if (err) {\n      console.error(\"Could not list the directory.\", err);\n      return res.status(500).send(\"Unable to list reports\");\n    }\n\n    const reports = files.filter(file => file.endsWith('.xlsx'));\n    return res.json(reports);\n  });\n});\n\nmodule.exports = router;"},{"path":"combinedBilling.js","content":"const express = require('express');\nconst router = express.Router();\nconst moment = require('moment-timezone');\nconst path = require('path');\nconst supabase = require('./utils/supabase');\n\nrouter.get('/', (req, res) => {\n  res.sendFile(path.join(__dirname, 'public/combinedBilling.html'));\n});\n\nrouter.get('/stats', async (req, res) => {\n  try {\n    const now = moment().tz('Africa/Johannesburg');\n    const startOfMonth = now.clone().startOf('month');\n\n    // Get billable records as source of truth\n    const { data: billingRecords, error: billingError } = await supabase\n      .from('billing_records')\n      .select('*')\n      .gte('message_timestamp', startOfMonth.format());\n\n    if (billingError) throw billingError;\n\n    // Filter inspire messages from billing records with tracking codes from messages_log\n    const { data: inspireMessages, error: inspireError } = await supabase\n      .from('messages_log')\n      .select('*')\n      .gte('timestamp', startOfMonth.format())\n      .not('tracking_code', 'is', null)\n      .not('tracking_code', 'eq', '');\n\n    if (inspireError) throw inspireError;\n\n    // Count customer messages properly\n    const customerMessagesCount = billingRecords.length;\n    \n    // Count inspire messages that have valid tracking codes\n    const inspireMessagesCount = inspireMessages ? inspireMessages.filter(m => m.tracking_code).length : 0;\n    \n    // Total messages is the combination of both\n    const totalMessages = customerMessagesCount + inspireMessagesCount;\n\n    // Group messages by user and session\n    const sessions = billingRecords.reduce((acc, record) => {\n      const mobileNumber = record.mobile_number || record.recipient_number;\n      if (!acc[mobileNumber]) {\n        acc[mobileNumber] = [];\n      }\n      acc[mobileNumber].push({\n        timestamp: moment(record.message_timestamp),\n        sessionStart: moment(record.session_start_time),\n        costs: {\n          utility: parseFloat(record.cost_utility) || 0,\n          carrier: parseFloat(record.cost_carrier) || 0,\n          mau: parseFloat(record.cost_mau) || 0,\n          total: parseFloat(record.total_cost) || 0\n        }\n      });\n      return acc;\n    }, {});\n\n    let billableSessions = 0;\n    let uniqueNumbers = 0;\n\n    // Calculate metrics per user\n    for (const [user, messages] of Object.entries(sessions)) {\n      if (!messages || messages.length === 0) continue;\n\n      // Sort messages by timestamp\n      messages.sort((a, b) => a.timestamp - b.timestamp);\n\n      // Group into sessions with 23h50m window\n      let activeSessions = [];\n      let currentSession = [messages[0]];\n\n      for (let i = 1; i < messages.length; i++) {\n        const timeDiff = messages[i].timestamp.diff(currentSession[0].timestamp, 'minutes');\n        if (timeDiff <= 1430) { // 23 hours and 50 minutes\n          currentSession.push(messages[i]);\n        } else {\n          activeSessions.push(currentSession);\n          currentSession = [messages[i]];\n        }\n      }\n      if (currentSession.length > 0) {\n        activeSessions.push(currentSession);\n      }\n\n      billableSessions += activeSessions.length;\n      uniqueNumbers++;\n    }\n\n    res.json({\n      inspire: {\n        count: inspireMessages.length,\n        uniqueClients: [...new Set(inspireMessages.map(m => m.client_guid))].length\n      },\n      customer: {\n        count: billingRecords.length,\n        uniqueNumbers: uniqueNumbers\n      },\n      billing: {\n        sessionCount: billableSessions,\n        sessionCost: billableSessions * 0.0176,\n        mauCost: uniqueNumbers * 0.06,\n        totalCost: (billableSessions * 0.0176) + (uniqueNumbers * 0.06)\n      },\n      totalMessages: totalMessages, // Added total messages\n      startDate: startOfMonth.format(),\n      endDate: now.format()\n    });\n  } catch (error) {\n    console.error('Error fetching combined stats:', error);\n    res.status(500).json({ error: 'Failed to fetch stats' });\n  }\n});\n\nrouter.get('/recent-messages', async (req, res) => {\n  try {\n    const { data, error } = await supabase\n      .from('messages_log')\n      .select('*')\n      .order('timestamp', { ascending: false })\n      .limit(20);\n\n    if (error) throw error;\n    res.json({ messages: data });\n  } catch (error) {\n    console.error('Error fetching recent messages:', error);\n    res.status(500).json({ error: 'Failed to fetch messages' });\n  }\n});\n\nmodule.exports = router;"},{"path":"create-project-zip.js","content":"\nconst fs = require('fs');\nconst path = require('path');\nconst { execSync } = require('child_process');\n\nconsole.log('🚀 Creating project zip file...');\n\ntry {\n  // Create tmp directory if it doesn't exist\n  const tmpDir = '/tmp';\n  if (!fs.existsSync(tmpDir)) {\n    fs.mkdirSync(tmpDir, { recursive: true });\n  }\n\n  // Get current date for filename\n  const date = new Date().toISOString().split('T')[0];\n  const zipName = `curo-inspire-${date}.zip`;\n  const zipPath = path.join(tmpDir, zipName);\n\n  // Files and directories to exclude\n  const excludeList = [\n    'node_modules',\n    '.git',\n    '.replit',\n    'replit.nix',\n    '.nvm',\n    '.config',\n    'attached_assets',\n    '*.log',\n    'tmp'\n  ];\n\n  // Create exclude string for zip command\n  const excludeArgs = excludeList.map(item => `-x \"${item}/*\"`).join(' ');\n\n  // Create the zip file\n  const zipCommand = `cd /home/runner/$(basename \"$PWD\") && zip -r \"${zipPath}\" . ${excludeArgs}`;\n  \n  console.log('📦 Creating zip with command:', zipCommand);\n  execSync(zipCommand, { stdio: 'inherit' });\n\n  console.log(`✅ Zip file created: ${zipPath}`);\n  console.log(`📁 File size: ${(fs.statSync(zipPath).size / 1024 / 1024).toFixed(2)} MB`);\n  \n  // List tmp directory contents\n  console.log('\\n📂 /tmp directory contents:');\n  const tmpContents = fs.readdirSync(tmpDir);\n  tmpContents.forEach(file => {\n    const filePath = path.join(tmpDir, file);\n    const stats = fs.statSync(filePath);\n    console.log(`  ${file} (${(stats.size / 1024).toFixed(2)} KB)`);\n  });\n\n} catch (error) {\n  console.error('❌ Error creating zip:', error.message);\n  process.exit(1);\n}\n"},{"path":"inspire-chat-state.js","content":"const axios = require('axios');\n\nconst INSPIRE_ENDPOINT = 'https://www.inspire-ohs.com/API/V3/WA/WAChatState';\nconst MAX_RETRIES = 3;\n\n/**\n * Push message status to Inspire endpoint with retry logic\n * @param {Object} statusData - The status data to push\n * @returns {boolean} - Success status\n */\nasync function pushToInspireChatState(statusData) {\n  const payload = {\n    apiKey: process.env.INSPIRE_API_KEY,\n    ClientGuid: statusData.clientGuid, // Use ClientGuid instead of messageId\n    recipientNumber: statusData.recipientNumber,\n    status: statusData.status,\n    timestamp: statusData.timestamp,\n    statusTimestamp: statusData.statusTimestamp,\n    channel: statusData.channel || 'whatsapp',\n    messageType: statusData.messageType,\n    trackingCode: statusData.trackingCode\n  };\n\n  let lastError = null;\n\n  for (let attempt = 1; attempt <= MAX_RETRIES; attempt++) {\n    try {\n      console.log(`🚀 Pushing to Inspire (attempt ${attempt}/${MAX_RETRIES}):`, {\n        ClientGuid: payload.ClientGuid,\n        status: payload.status,\n        recipientNumber: payload.recipientNumber\n      });\n\n      const response = await axios.post(INSPIRE_ENDPOINT, payload, {\n        headers: {\n          'Content-Type': 'application/json'\n        },\n        timeout: 10000 // 10 second timeout\n      });\n\n      console.log(`✅ Inspire push successful (attempt ${attempt}):`, {\n        ClientGuid: payload.ClientGuid,\n        status: response.status,\n        data: response.data\n      });\n\n      return true;\n\n    } catch (error) {\n      lastError = error;\n      console.error(`❌ Inspire push failed (attempt ${attempt}/${MAX_RETRIES}):`, {\n        ClientGuid: payload.ClientGuid,\n        error: error.message,\n        status: error.response?.status,\n        data: error.response?.data\n      });\n\n      // Don't retry on client errors (4xx)\n      if (error.response?.status >= 400 && error.response?.status < 500) {\n        console.log(`🛑 Client error detected, not retrying: ${error.response.status}`);\n        break;\n      }\n\n      // Wait before retry (exponential backoff)\n      if (attempt < MAX_RETRIES) {\n        const delay = Math.pow(2, attempt) * 1000; // 2s, 4s, 8s\n        console.log(`⏳ Waiting ${delay}ms before retry...`);\n        await new Promise(resolve => setTimeout(resolve, delay));\n      }\n    }\n  }\n\n  console.error(`💥 All Inspire push attempts failed for message ${payload.ClientGuid}:`, lastError?.message);\n  return false;\n}\n\nmodule.exports = {\n  pushToInspireChatState\n};"},{"path":"may-billing-report.js","content":"\nconst express = require('express');\nconst router = express.Router();\nconst moment = require('moment-timezone');\nconst { createClient } = require('@supabase/supabase-js');\n\nconst supabase = createClient(process.env.SUPABASE_URL, process.env.SUPABASE_SERVICE_ROLE_KEY);\n\nrouter.get('/may-2025-report', async (req, res) => {\n  try {\n    // Set May 2025 date range\n    const startOfMay = moment.tz('2025-05-01', 'Africa/Johannesburg').startOf('day');\n    const endOfMay = moment.tz('2025-05-31', 'Africa/Johannesburg').endOf('day');\n\n    console.log('Generating May 2025 billing report...');\n    console.log('Date range:', startOfMay.format(), 'to', endOfMay.format());\n\n    // Get all billing records for May 2025\n    let allBillingRecords = [];\n    let page = 0;\n    const pageSize = 1000;\n\n    while (true) {\n      const { data: pageRecords, error: pageError } = await supabase\n        .from('billing_records')\n        .select('*')\n        .gte('message_timestamp', startOfMay.format())\n        .lte('message_timestamp', endOfMay.format())\n        .order('message_timestamp', { ascending: false })\n        .range(page * pageSize, (page + 1) * pageSize - 1);\n\n      if (pageError) throw pageError;\n      if (!pageRecords || pageRecords.length === 0) break;\n\n      allBillingRecords = [...allBillingRecords, ...pageRecords];\n      if (pageRecords.length < pageSize) break;\n      page++;\n    }\n\n    const billingRecords = allBillingRecords;\n    console.log(`Found ${billingRecords.length} billing records for May 2025`);\n\n    if (!billingRecords || billingRecords.length === 0) {\n      return res.json({\n        period: 'May 2025',\n        totalMessages: 0,\n        billableSessions: 0,\n        monthlyActiveUsers: 0,\n        sessionCost: 0,\n        mauCost: 0,\n        totalCost: 0,\n        carrierCount: 0,\n        carrierTotal: 0,\n        utilityCount: 0,\n        utilityTotal: 0,\n        recentSessions: [],\n        message: 'No billing data found for May 2025'\n      });\n    }\n\n    // Group messages by user and session (exact same logic as billing.js)\n    const sessions = billingRecords.reduce((acc, record) => {\n      const mobileNumber = record.mobile_number || record.recipient_number;\n      if (!acc[mobileNumber]) {\n        acc[mobileNumber] = [];\n      }\n      acc[mobileNumber].push({\n        timestamp: moment(record.message_timestamp),\n        sessionStart: moment(record.session_start_time),\n        costs: {\n          utility: parseFloat(record.cost_utility) || 0,\n          carrier: parseFloat(record.cost_carrier) || 0,\n          mau: parseFloat(record.cost_mau) || 0,\n          total: parseFloat(record.total_cost) || 0\n        },\n        is_mau_charged: record.is_mau_charged\n      });\n      return acc;\n    }, {});\n\n    // Calculate metrics (exact same logic as billing.js)\n    let carrierCount = 0;\n    let carrierTotal = 0;\n    let utilityCount = 0;\n    let utilityTotal = 0;\n    let sessionCount = 0;\n\n    for (const [user, messages] of Object.entries(sessions)) {\n      if (!messages || messages.length === 0) continue;\n\n      messages.sort((a, b) => a.timestamp - b.timestamp);\n\n      let activeSessions = [];\n      let currentSession = [messages[0]];\n\n      for (let i = 1; i < messages.length; i++) {\n        const timeDiff = messages[i].timestamp.diff(currentSession[0].timestamp, 'minutes');\n        if (timeDiff <= 1430) { // 23 hours and 50 minutes\n          currentSession.push(messages[i]);\n        } else {\n          activeSessions.push(currentSession);\n          currentSession = [messages[i]];\n        }\n      }\n      if (currentSession.length > 0) {\n        activeSessions.push(currentSession);\n      }\n\n      sessionCount += activeSessions.length;\n      carrierCount += activeSessions.length;\n      utilityCount += activeSessions.length;\n      carrierTotal += activeSessions.length * 0.01;\n      utilityTotal += activeSessions.length * 0.0076;\n    }\n\n    const sessionCost = carrierTotal + utilityTotal;\n    const mauCost = billingRecords\n      .filter(record => record.is_mau_charged === true)\n      .reduce((sum, record) => sum + (parseFloat(record.cost_mau) || 0), 0);\n    const totalCost = sessionCost + mauCost;\n    const monthlyActiveUsers = Object.keys(sessions).length;\n\n    // Get recent sessions (top 10)\n    const recentSessions = billingRecords.slice(0, 10).map(session => ({\n      message_timestamp: session.message_timestamp,\n      mobile_number: session.mobile_number,\n      whatsapp_message_id: session.whatsapp_message_id,\n      cost_utility: parseFloat(session.cost_utility) || 0,\n      cost_carrier: parseFloat(session.cost_carrier) || 0,\n      cost_mau: parseFloat(session.cost_mau) || 0,\n      total_cost: parseFloat(session.total_cost) || 0\n    }));\n\n    // Format the response exactly like billing dashboard\n    const report = {\n      period: 'May 2025',\n      dateRange: `${startOfMay.format('YYYY-MM-DD')} to ${endOfMay.format('YYYY-MM-DD')}`,\n      totalMessages: billingRecords.length,\n      billableSessions: sessionCount,\n      monthlyActiveUsers: monthlyActiveUsers,\n      sessionCost: sessionCost,\n      mauCost: mauCost,\n      totalCost: totalCost,\n      carrierCount: carrierCount,\n      carrierTotal: carrierTotal,\n      utilityCount: utilityCount,\n      utilityTotal: utilityTotal,\n      recentSessions: recentSessions,\n      generatedAt: moment().tz('Africa/Johannesburg').format('YYYY-MM-DD HH:mm:ss')\n    };\n\n    // Format output exactly like billing dashboard\n    console.log('\\n==========================================');\n    console.log('           MAY 2025 BILLING REPORT       ');\n    console.log('==========================================');\n    console.log(`Billing Period: ${report.period}`);\n    console.log(`Date Range: ${report.dateRange}`);\n    console.log(`Generated: ${report.generatedAt} (Africa/Johannesburg)`);\n    console.log('==========================================');\n    console.log('');\n    console.log('📊 BILLING STATISTICS:');\n    console.log('');\n    console.log(`Total Messages: ${report.totalMessages}`);\n    console.log(`Billable Sessions: ${report.billableSessions}`);\n    console.log(`Monthly Active Users: ${report.monthlyActiveUsers}`);\n    console.log('');\n    console.log('💰 COST BREAKDOWN:');\n    console.log('');\n    console.log(`Session Cost: $${report.sessionCost.toFixed(4)}`);\n    console.log(`  └── Carrier Fees: ${report.carrierCount} sessions × $0.0100 = $${report.carrierTotal.toFixed(4)}`);\n    console.log(`  └── Utility Fees: ${report.utilityCount} sessions × $0.0076 = $${report.utilityTotal.toFixed(4)}`);\n    console.log('');\n    console.log(`MAU Cost: $${report.mauCost.toFixed(4)}`);\n    console.log(`  └── ${report.monthlyActiveUsers} unique users × $0.0600 = $${(report.monthlyActiveUsers * 0.06).toFixed(4)}`);\n    console.log('');\n    console.log(`TOTAL COST: $${report.totalCost.toFixed(4)}`);\n    console.log('');\n    console.log('📋 RECENT SESSIONS (Top 10):');\n    console.log('');\n    \n    if (report.recentSessions.length === 0) {\n      console.log('No sessions found');\n    } else {\n      console.log('Timestamp                | Mobile Number    | WhatsApp ID                              | Utility  | Carrier  | MAU      | Total   ');\n      console.log('-------------------------|------------------|------------------------------------------|----------|----------|----------|----------');\n      \n      report.recentSessions.forEach(session => {\n        const timestamp = moment(session.message_timestamp).tz('Africa/Johannesburg').format('DD/MM/YYYY HH:mm:ss');\n        const mobile = session.mobile_number.padEnd(16);\n        const whatsappId = (session.whatsapp_message_id || 'N/A').substring(0, 40).padEnd(40);\n        const utility = `$${session.cost_utility.toFixed(4)}`.padStart(8);\n        const carrier = `$${session.cost_carrier.toFixed(4)}`.padStart(8);\n        const mau = `$${session.cost_mau.toFixed(4)}`.padStart(8);\n        const total = `$${session.total_cost.toFixed(4)}`.padStart(8);\n        \n        console.log(`${timestamp} | ${mobile} | ${whatsappId} | ${utility} | ${carrier} | ${mau} | ${total}`);\n      });\n    }\n    \n    console.log('');\n    console.log('==========================================');\n\n    res.json(report);\n\n  } catch (error) {\n    console.error('Error generating May 2025 billing report:', error);\n    res.status(500).json({ error: 'Failed to generate May 2025 billing report' });\n  }\n});\n\nmodule.exports = router;\n"},{"path":"package-lock.json","content":"{\n  \"name\": \"curo-inspire\",\n  \"version\": \"1.0.0\",\n  \"lockfileVersion\": 2,\n  \"requires\": true,\n  \"packages\": {\n    \"\": {\n      \"name\": \"curo-inspire\",\n      \"version\": \"1.0.0\",\n      \"dependencies\": {\n        \"@supabase/supabase-js\": \"^2.49.1\",\n        \"axios\": \"^1.4.0\",\n        \"cors\": \"^2.8.5\",\n        \"dotenv\": \"^16.0.3\",\n        \"esprima\": \"^4.0.1\",\n        \"express\": \"^4.18.2\",\n        \"express-basic-auth\": \"^1.2.1\",\n        \"luxon\": \"^3.5.0\",\n        \"moment-timezone\": \"^0.5.47\",\n        \"node-cron\": \"^4.0.7\",\n        \"nodemailer\": \"^7.0.3\",\n        \"pg\": \"^8.16.2\",\n        \"uuid\": \"^11.1.0\",\n        \"xlsx\": \"^0.18.5\"\n      },\n      \"devDependencies\": {\n        \"autoprefixer\": \"^10.4.21\",\n        \"postcss\": \"^8.5.3\",\n        \"tailwindcss\": \"^4.1.3\"\n      },\n      \"engines\": {\n        \"node\": \"20.x\"\n      }\n    },\n    \"node_modules/@supabase/auth-js\": {\n      \"version\": \"2.69.1\",\n      \"resolved\": \"https://registry.npmjs.org/@supabase/auth-js/-/auth-js-2.69.1.tgz\",\n      \"integrity\": \"sha512-FILtt5WjCNzmReeRLq5wRs3iShwmnWgBvxHfqapC/VoljJl+W8hDAyFmf1NVw3zH+ZjZ05AKxiKxVeb0HNWRMQ==\",\n      \"dependencies\": {\n        \"@supabase/node-fetch\": \"^2.6.14\"\n      }\n    },\n    \"node_modules/@supabase/functi