n\": \"#00fa9a\",\n  \"mediumturquoise\": \"#48d1cc\",\n  \"mediumvioletred\": \"#c71585\",\n  \"midnightblue\": \"#191970\",\n  \"mintcream\": \"#f5fffa\",\n  \"mistyrose\": \"#ffe4e1\",\n  \"moccasin\": \"#ffe4b5\",\n  \"navajowhite\": \"#ffdead\",\n  \"navy\": \"#000080\",\n  \"oldlace\": \"#fdf5e6\",\n  \"olive\": \"#808000\",\n  \"olivedrab\": \"#6b8e23\",\n  \"orange\": \"#ffa500\",\n  \"orangered\": \"#ff4500\",\n  \"orchid\": \"#da70d6\",\n  \"palegoldenrod\": \"#eee8aa\",\n  \"palegreen\": \"#98fb98\",\n  \"paleturquoise\": \"#afeeee\",\n  \"palevioletred\": \"#db7093\",\n  \"papayawhip\": \"#ffefd5\",\n  \"peachpuff\": \"#ffdab9\",\n  \"peru\": \"#cd853f\",\n  \"pink\": \"#ffc0cb\",\n  \"plum\": \"#dda0dd\",\n  \"powderblue\": \"#b0e0e6\",\n  \"purple\": \"#800080\",\n  \"rebeccapurple\": \"#663399\",\n  \"red\": \"#ff0000\",\n  \"rosybrown\": \"#bc8f8f\",\n  \"royalblue\": \"#4169e1\",\n  \"saddlebrown\": \"#8b4513\",\n  \"salmon\": \"#fa8072\",\n  \"sandybrown\": \"#f4a460\",\n  \"seagreen\": \"#2e8b57\",\n  \"seashell\": \"#fff5ee\",\n  \"sienna\": \"#a0522d\",\n  \"silver\": \"#c0c0c0\",\n  \"skyblue\": \"#87ceeb\",\n  \"slateblue\": \"#6a5acd\",\n  \"slategray\": \"#708090\",\n  \"slategrey\": \"#708090\",\n  \"snow\": \"#fffafa\",\n  \"springgreen\": \"#00ff7f\",\n  \"steelblue\": \"#4682b4\",\n  \"tan\": \"#d2b48c\",\n  \"teal\": \"#008080\",\n  \"thistle\": \"#d8bfd8\",\n  \"tomato\": \"#ff6347\",\n  \"turquoise\": \"#40e0d0\",\n  \"violet\": \"#ee82ee\",\n  \"wheat\": \"#f5deb3\",\n  \"white\": \"#ffffff\",\n  \"whitesmoke\": \"#f5f5f5\",\n  \"yellow\": \"#ffff00\",\n  \"yellowgreen\": \"#9acd32\"\n}"},{"path":"src/pages/index.hbs","content":"<!DOCTYPE html>\n<html lang=\"en\">\n  <head>\n    \n    <!-- \n      This is the main Handlebars template for the site \n      - When the user visits the homepage or submits a color the app calls the endpoints in server.js\n      - The server script passes data in here and the Handlebars code builds it into the HTML page\n    -->\n    \n    <meta charset=\"utf-8\" />\n    <link rel=\"icon\" href=\"https://glitch.com/favicon.ico\" />\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1\" />\n\n    <title>{{seo.title}}</title>\n    \n    <!-- Meta tags for SEO and social sharing -->\n    <link rel=\"canonical\" href=\"{{seo.url}}\">\n    <meta name=\"description\" content=\"{{seo.description}}\">\n    <meta property=\"og:title\" content=\"{{seo.title}}\">\n    <meta property=\"og:type\" content=\"article\">\n    <meta property=\"og:url\" content=\"{{seo.url}}\">\n    <meta property=\"og:description\" content=\"{{seo.description}}\">\n    <meta property=\"og:image\" content=\"{{seo.image}}\">\n    <meta name=\"twitter:card\" content=\"summary\">\n\n    <!-- Import the webpage's stylesheet -->\n    <link rel=\"stylesheet\" href=\"/style.css\" />\n\n    <!-- This is a handlebars IF statement\n         Checking for a custom \"color\" parameter from server.js -->\n    {{#if color}} \n    <!-- If there is a color value, update the style for the page title -->\n    <style>\n      .title {\n        color: {{color}};\n      }\n    </style>\n    {{/if}}    \n  </head>\n  <body>\n    <div class=\"wrapper\">\n      <div class=\"content\" role=\"main\">\n        \n        <!-- This is the start of content for our page -->\n        <h1 class=\"title\">Hello Node!</h1>\n        \n        <!-- Add text indicating that we've changed color OR had an error -->\n        {{#if color}}\n          <p class=\"color-info\">\n            Now available in <b>{{color}}</b>!\n          </p>\n        {{/if}}  \n        \n        {{#if colorError}}\n          <p class=\"color-info\">\n            \n            <!-- The server script passes colorError if the user submission can't be matched -->\n            Hmm. Couldn't find <b>\"{{colorError}}\"</b>. \n            <a href=\"?randomize=go\">Try a random color?</a>\n          </p>\n        {{/if}}\n        \n        <div class=\"color-form\">\n          \n          <!-- Our default paragraph/message -->\n          {{#unless colorError}}\n            {{#unless color}}\n          \n            <!-- There are fancier ways to do this, \n                 but nesting two \"#unless\" statements sets up an \n                 \"if these are both missing...\" condition -->\n            <p>\n              Learn about communication with a server by talking about color.\n            </p>\n            {{/unless}}\n          {{/unless}}\n          \n          <!-- The \"What's your favorite color?\" form sends a POST request to the server\n               Check server.js to see how it works -->\n          <form class=\"color-search\" method=\"post\">\n              <label for=\"color\">\n                \n                <!-- We use the handlebars \"#if\" statement so the form can adapt to its situation -->\n                {{#if color}}\n                  Try another color?<br>\n                {{else}}\n                  What's your favorite color?<br>\n                {{/if}}\n                <input id=\"color\" name=\"color\" required=\"required\" type=\"text\">\n              </label>\n            \n            <!-- If the user submits a value through the form \n                 it'll be passed to the server in the request body  -->\n            <button type=\"submit\">Submit</button>\n          </form>\n          \n          <!-- This randomize link communicates with the server (server.js) \n               using a querystring and GET request instead of a form POST -->\n          Can't decide? <a href=\"?randomize=go\">Use a random color!</a>\n        </div>\n        \n        <div class=\"illo-container\">\n          <img src=\"https://cdn.glitch.com/c62efef6-1e75-45cf-b248-afeccdda9477%2Fillustration.svg?v=1618196069568\" class=\"illustration\" alt=\"Node logo\" />\n        </div>\n        \n        <!-- Instructions on using this project -->\n        <div class=\"instructions\">\n          <h2>\n            Using this project\n          </h2>\n          <p>\n            This is the Glitch <strong>Hello Node</strong> project. You can\n            use it to build your own app. Check out README.md in the editor for\n            more info and next steps you can take!\n          </p>\n        </div>\n        \n      </div>\n    </div>\n    <!-- The footer holds our remix button — you can use it for structure or cut it out ✂ -->\n    <footer class=\"footer\">\n      <div class=\"links\"></div>\n      <a\n        class=\"btn--remix\"\n        target=\"_top\"\n        href=\"https://glitch.com/edit/#!/remix/glitch-hello-node\"\n      >\n        <img\n          src=\"https://cdn.glitch.com/605e2a51-d45f-4d87-a285-9410ad350515%2FLogo_Color.svg?v=1618199565140\"\n          alt=\"\"\n        />\n        Remix on Glitch\n      </a>\n    </footer>\n  </body>\n</html>\n"},{"path":"src/seo.json","content":"{\n  \"glitch-help-instructions\": \"For a custom domain, change the 'url' parameter from 'glitch-default' to your domain _without_ a traling slash, like 'https://www.example.com'\",\n  \"title\": \"Hello Node!\",\n  \"description\": \"A simple Node site built with Glitch. Remix it to get your own.\",\n  \"url\": \"glitch-default\",\n  \"image\": \"https://cdn.glitch.com/605e2a51-d45f-4d87-a285-9410ad350515%2Fhello-node-social.png?v=1618161394375\"\n}\n"},{"path":"tailwind.config.js","content":"\n/** @type {import('tailwindcss').Config} */\nmodule.exports = {\n  content: [\"./public/**/*.{html,js}\"],\n  theme: {\n    extend: {},\n  },\n  plugins: [],\n}\n"},{"path":"test-analysis.sh","content":"\n#!/bin/bash\n\n# Test the project analysis endpoint\nTOKEN=\"${ANALYSIS_TOKEN}\"\nURL=\"https://$(echo $REPL_SLUG)-$(echo $REPL_OWNER).replit.dev/api/project-analysis\"\n\necho \"🔍 Testing project analysis endpoint...\"\necho \"URL: $URL\"\n\n# Pull the analysis\ncurl -s -H \"x-analysis-token: $TOKEN\" \"$URL\" -o analysis.json\n\nif [ $? -eq 0 ] && [ -s analysis.json ]; then\n    echo \"✅ Analysis downloaded successfully\"\n    \n    # Show file count\n    FILE_COUNT=$(jq -r '.files | length' analysis.json)\n    echo \"📁 Total files: $FILE_COUNT\"\n    \n    # Show first 30 file paths\n    echo -e \"\\n📂 First 30 file paths:\"\n    jq -r '.files[].path' analysis.json | head -n 30\n    \n    # Lines of code by extension\n    echo -e \"\\n📊 Lines of code by extension:\"\n    jq -r '.files[] | \"\\(.path)\\t\\(.content|split(\"\\n\")|length)\"' analysis.json \\\n      | awk -F'\\t' '{ext=$1; sub(/.*\\./,\"\",ext); loc[ext]+=$2} END{for(e in loc) print e,loc[e]}' \\\n      | sort -k2 -nr | head -10\n    \n    echo -e \"\\n💾 Analysis saved to analysis.json\"\nelse\n    echo \"❌ Failed to download analysis\"\n    cat analysis.json 2>/dev/null || echo \"No response received\"\nfi\n"},{"path":"test-healer.js","content":"\nconst CodeHealer = require('./utils/codeHealer');\nconst codeHealer = new CodeHealer();\n\n// Test run of the CodeHealer\nasync function testHealer() {\n    console.log('🔍 Running CodeHealer test...\\n');\n    \n    const issues = await codeHealer.heal();\n    \n    console.log('Found issues:');\n    issues.forEach(issue => {\n        console.log('\\n-------------------');\n        console.log(`File: ${issue.file}`);\n        console.log(`Type: ${issue.type}`);\n        console.log(`Description: ${issue.description}`);\n        console.log(`Suggested Fix: ${issue.fix}`);\n        console.log(`Auto-fixable: ${issue.autoFixable}`);\n    });\n}\n\ntestHealer().catch(console.error);\n"},{"path":"utils/codeHealer.js","content":"\nconst { createClient } = require('@supabase/supabase-js');\nconst esprima = require('esprima');\nconst fs = require('fs');\nconst path = require('path');\n\nclass CodeHealer {\n  constructor() {\n    this.supabase = createClient(process.env.SUPABASE_URL, process.env.SUPABASE_SERVICE_ROLE_KEY);\n    this.knownPatterns = {\n      'undefinedCheck': /(!==\\s*undefined)/,\n      'consoleLog': /console\\.log\\(/,\n      'uncaughtPromise': /\\.then\\(.*\\)(?!\\.catch)/\n    };\n  }\n\n  async heal() {\n    const issues = [];\n    issues.push(...await this.analyzeCode());\n    issues.push(...await this.checkDatabaseSchema());\n    return this.suggestFixes(issues);\n  }\n\n  async analyzeCode(dir = '.') {\n    const issues = [];\n    const files = fs.readdirSync(dir).filter(f => f.endsWith('.js'));\n    \n    for (const file of files) {\n      const filePath = path.join(dir, file);\n      const code = fs.readFileSync(filePath, 'utf8');\n      \n      try {\n        const syntaxErrors = esprima.parseScript(code, { tolerant: true }).errors;\n        if (syntaxErrors.length) {\n          issues.push({\n            type: 'syntax',\n            file: filePath,\n            errors: syntaxErrors\n          });\n        }\n      } catch (error) {\n        issues.push({\n          type: 'error',\n          file: filePath,\n          error: error.message\n        });\n      }\n    }\n    return issues;\n  }\n\n  async checkDatabaseSchema() {\n    const issues = [];\n    const { data: tables, error } = await this.supabase\n      .from('information_schema.columns')\n      .select('*');\n    \n    if (error) return issues;\n\n    tables?.forEach(column => {\n      if (column.column_name.endsWith('_id') && !column.is_nullable) {\n        issues.push({\n          type: 'database',\n          table: column.table_name,\n          column: column.column_name,\n          issue: 'Missing index on foreign key'\n        });\n      }\n    });\n\n    return issues;\n  }\n\n  async suggestFixes(issues) {\n    return issues.map(issue => ({\n      file: issue.file,\n      type: issue.type,\n      description: this.getDescription(issue),\n      fix: this.getFix(issue),\n      autoFixable: this.isAutoFixable(issue)\n    }));\n  }\n\n  getDescription(issue) {\n    const descriptions = {\n      syntax: 'Syntax error in code',\n      pattern: `Found potentially problematic pattern: ${issue.pattern}`,\n      database: `Database issue: ${issue.issue}`,\n      error: `Error analyzing file: ${issue.error}`\n    };\n    return descriptions[issue.type];\n  }\n\n  getFix(issue) {\n    switch(issue.type) {\n      case 'pattern':\n        return this.getPatternFix(issue.pattern);\n      case 'database':\n        return `CREATE INDEX idx_${issue.column} ON ${issue.table} (${issue.column});`;\n      default:\n        return 'Manual review required';\n    }\n  }\n\n  isAutoFixable(issue) {\n    return ['pattern', 'database'].includes(issue.type);\n  }\n\n  getPatternFix(pattern) {\n    const fixes = {\n      'undefinedCheck': 'Use optional chaining (?.) instead',\n      'consoleLog': 'Remove console.log in production',\n      'uncaughtPromise': 'Add .catch() handler to promise'\n    };\n    return fixes[pattern];\n  }\n}\n\nmodule.exports = CodeHealer;\n"},{"path":"utils/supabase.js","content":"\nconst { createClient } = require('@supabase/supabase-js');\n\nconst supabase = createClient(\n  process.env.SUPABASE_URL,\n  process.env.SUPABASE_SERVICE_ROLE_KEY,\n  {\n    auth: { persistSession: false }\n  }\n);\n\nmodule.exports = supabase;\n"},{"path":"whatsapp-status-webhook.js","content":"const express = require('express');\nconst { createClient } = require('@supabase/supabase-js');\nconst { pushToInspireChatState } = require('./inspire-chat-state');\nconst router = express.Router();\n\nconst supabase = createClient(process.env.SUPABASE_URL, process.env.SUPABASE_SERVICE_ROLE_KEY);\n\nrouter.use(express.json());\n\nconst insertStatusToDb = async (statusDetails) => {\n  console.log('Processing status update:', JSON.stringify(statusDetails, null, 2));\n\n  try {\n    const { messageId, recipientId, status, timestamp } = statusDetails;\n\n    if (!messageId || !recipientId || !status) {\n      throw new Error('Missing required fields for status update');\n    }\n\n    const messageTime = new Date(timestamp * 1000);\n    const currentTimestamp = new Date().toISOString();\n\n    // Get the last session for this number in the past 23h50m (for session grouping)\n    const sessionWindowMinutes = 1430; // 23h50m\n    const messageTimeDate = new Date(messageTime);\n    const sessionCutoff = new Date(messageTimeDate.getTime() - (sessionWindowMinutes * 60 * 1000));\n\n    // Get start of current month (for MAU checking)\n    const currentMonth = new Date(messageTimeDate.getFullYear(), messageTimeDate.getMonth(), 1);\n\n    console.log('Processing message:', {\n      number: recipientId,\n      messageTime: messageTimeDate.toISOString(),\n      sessionCutoff: sessionCutoff.toISOString(),\n      currentMonthStart: currentMonth.toISOString()\n    });\n\n    // Check for existing session within 23h50m window\n    const { data: lastSession } = await supabase\n      .from(\"billing_records\")\n      .select(\"*\")\n      .eq(\"mobile_number\", recipientId)\n      .gte(\"message_timestamp\", sessionCutoff.toISOString())\n      .order(\"message_timestamp\", { ascending: false })\n      .limit(1);\n\n    // Check for ANY billing record for this number in current month (for MAU)\n    const { data: monthlyRecord } = await supabase\n      .from(\"billing_records\")\n      .select(\"is_mau_charged\")\n      .eq(\"mobile_number\", recipientId)\n      .gte(\"message_timestamp\", currentMonth.toISOString())\n      .order(\"message_timestamp\", { ascending: false })\n      .limit(1);\n\n    if (lastSession?.[0]) {\n      console.log('Found existing session:', {\n        number: recipientId,\n        lastMessageTime: lastSession[0].message_timestamp,\n        withinWindow: true\n      });\n    }\n\n    console.log('MAU check:', {\n      messageTime: messageTimeDate.toISOString(),\n      monthStart: currentMonth.toISOString(),\n      hasMonthlyRecord: !!monthlyRecord?.[0],\n      alreadyChargedMAU: monthlyRecord?.[0]?.is_mau_charged\n    });\n\n    // Determine if this is first message of the month (for MAU charging)\n    // Only charge MAU if there's NO previous record for this number in current month\n    const isFirstOfMonth = !monthlyRecord?.[0];\n\n    // Only create billing record if:\n    // 1. No session within 23h50m window (new session), OR\n    // 2. First message of the month for this number (MAU charge needed)\n    const isNewSession = !lastSession?.[0];\n\n    if (isNewSession || isFirstOfMonth) {\n      // FIXED LOGIC: MAU and Carrier are mutually exclusive\n      // If first message of month -> charge MAU + Utility, no Carrier\n      // If new session (not first of month) -> charge Carrier + Utility, no MAU\n\n      const billingRecord = {\n        whatsapp_message_id: messageId,\n        mobile_number: recipientId,\n        message_timestamp: messageTime.toISOString(),\n        session_start_time: messageTime.toISOString(),\n        cost_utility: (isNewSession || isFirstOfMonth) ? 0.0076 : 0.0000,\n        cost_carrier: (isNewSession && !isFirstOfMonth) ? 0.0100 : 0.0000,\n        cost_mau: isFirstOfMonth ? 0.0600 : 0.0000,\n        total_cost: 0.0076 + (isFirstOfMonth ? 0.0600 : (isNewSession ? 0.0100 : 0.0000)),\n        is_mau_charged: isFirstOfMonth,\n        message_month: `${messageTime.getFullYear()}-${String(messageTime.getMonth() + 1).padStart(2, '0')}-01`\n      };\n\n      console.log('Creating billing record:', {\n        mobile: recipientId,\n        messageTime: messageTime.toISOString(),\n        isNewSession,\n        isFirstOfMonth,\n        costs: {\n          utility: billingRecord.cost_utility,\n          carrier: billingRecord.cost_carrier,\n          mau: billingRecord.cost_mau,\n          total: billingRecord.total_cost\n        }\n      });\n\n      const { error: billingError } = await supabase\n        .from(\"billing_records\")\n        .insert([billingRecord]);\n\n      if (billingError) {\n        console.error('Failed to insert billing record:', billingError);\n      }\n    } else {\n      console.log('Message part of existing session - no billing record needed:', {\n        mobile: recipientId,\n        sessionStart: lastSession[0].session_start_time\n      });\n    }\n\n    const newStatusRecord = {\n      original_wamid: messageId,\n      mobile_number: recipientId,\n      channel: \"whatsapp\",\n      status: status,\n      timestamp: messageTime.toISOString(),\n      status_timestamp: currentTimestamp,\n      message_type: 'status_update'\n    };\n\n    const { data, error } = await supabase\n      .from(\"messages_log\")\n      .insert([newStatusRecord])\n      .select();\n\n    if (error) {\n      console.error('Database error details:', {\n        error,\n        attemptedRecord: newStatusRecord,\n        messageId: statusDetails.messageId,\n        status: statusDetails.status\n      });\n      throw error;\n    }\n\n    console.log('Successfully inserted status:', {\n      wamid: messageId,\n      status: status,\n      timestamp: currentTimestamp\n    });\n\n    // Check if this status update is for a message that originated from Inspire\n    // Look for the most recent message to this number that has both tracking code and client_guid\n    // Include both \"pending\" and \"sent\" status messages since messages start as pending\n    const { data: originalMessage } = await supabase\n      .from(\"messages_log\")\n      .select(\"tracking_code, original_wamid, client_guid\")\n      .eq(\"mobile_number\", recipientId)\n      .in(\"status\", [\"pending\", \"sent\"])\n      .not(\"tracking_code\", \"is\", null)\n      .not(\"client_guid\", \"is\", null)\n      .order(\"timestamp\", { ascending: false })\n      .limit(1)\n      .single();\n\n    let pushSuccess = null;\n    \n    if (originalMessage?.client_guid) {\n      console.log(`📤 Status update for message ${messageId} - found Inspire message with ClientGuid ${originalMessage.client_guid} - pushing to Inspire`);\n      \n      pushSuccess = await pushToInspireChatState({\n        clientGuid: originalMessage.client_guid, // Use ClientGuid instead of messageId\n        recipientNumber: recipientId,\n        status: status,\n        timestamp: timestamp,\n        statusTimestamp: currentTimestamp,\n        channel: 'whatsapp',\n        messageType: 'status_update',\n        trackingCode: originalMessage.tracking_code\n      });\n\n      // Update the record with Inspire push results\n      const { error: updateError } = await supabase\n        .from(\"messages_log\")\n        .update({\n          inspire_push_status: pushSuccess ? 'success' : 'failed'\n        })\n        .eq('id', data[0].id);\n\n      if (updateError) {\n        console.error('Failed to update Inspire push status:', updateError);\n      } else {\n        console.log(`✅ Updated Inspire push status: ${pushSuccess ? 'success' : 'failed'} for ClientGuid ${originalMessage.client_guid}`);\n      }\n    } else {\n      console.log(`⏭️ Status update for message ${messageId} - no recent Inspire-originated message with ClientGuid found for ${recipientId} - skipping push`);\n      \n      // Mark as skipped in database\n      const { error: updateError } = await supabase\n        .from(\"messages_log\")\n        .update({\n          inspire_push_status: 'skipped'\n        })\n        .eq('id', data[0].id);\n\n      if (updateError) {\n        console.error('Failed to update skipped status:', updateError);\n      }\n    }\n\n    return data;\n  } catch (err) {\n    console.error('Failed to insert status:', err);\n    throw err;\n  }\n};\n\n// 360dialog webhook verification handler\nrouter.get('/', (req, res) => {\n  console.log(\"=== INCOMING WHATSAPP VERIFICATION REQUEST ===\");\n  console.log(\"Query params:\", req.query);\n\n  // Always return 200 since 360dialog handles authentication\n  res.sendStatus(200);\n});\n\nrouter.post('/', async (req, res) => {\n  const requestTimestamp = new Date().toISOString();\n  console.log(\"\\n======== WHATSAPP WEBHOOK ANALYSIS ========\");\n  console.log(`Timestamp: ${requestTimestamp}`);\n  console.log(\"\\n=== REQUEST HEADERS ===\");\n  console.log(JSON.stringify(req.headers, null, 2));\n\n  console.log(\"\\n=== FULL PAYLOAD STRUCTURE ===\");\n  const payload = req.body;\n  console.log(JSON.stringify(payload, null, 2));\n\n  if (payload?.entry?.[0]?.changes?.[0]?.value) {\n    const value = payload.entry[0].changes[0].value;\n    console.log(\"\\n=== MESSAGE STRUCTURE ANALYSIS ===\");\n    console.log(\"Has Statuses:\", !!value.statuses);\n    console.log(\"Status Count:\", value.statuses?.length || 0);\n    console.log(\"Has Messages:\", !!value.messages);\n    console.log(\"Message Count:\", value.messages?.length || 0);\n    console.log(\"Metadata:\", value.metadata || \"None\");\n\n    if (value.statuses) {\n      console.log(\"\\n=== STATUS DETAILS ===\");\n      value.statuses.forEach((status, index) => {\n        console.log(`\\nStatus #${index + 1}:`);\n        console.log(JSON.stringify(status, null, 2));\n      });\n    }\n\n    if (value.messages) {\n      console.log(\"\\n=== MESSAGE DETAILS ===\");\n      value.messages.forEach((msg, index) => {\n        console.log(`\\nMessage #${index + 1}:`);\n        console.log(JSON.stringify(msg, null, 2));\n      });\n    }\n  }\n  console.log(\"\\n=========================================\");\n\n  // Detailed payload parsing\n  const entry = req.body?.entry?.[0];\n  const changes = entry?.changes?.[0];\n  const value = changes?.value;\n\n  console.log(\"\\nPayload Structure Analysis:\");\n  console.log(\"Entry Structure:\", {\n    hasEntry: !!req.body?.entry,\n    entryLength: req.body?.entry?.length,\n    firstEntry: entry\n  });\n\n  console.log(\"Changes Structure:\", {\n    hasChanges: !!entry?.changes,\n    changesLength: entry?.changes?.length,\n    firstChange: changes,\n    field: changes?.field\n  });\n\n  console.log(\"Value Contents:\", {\n    hasStatuses: !!value?.statuses,\n    statusesLength: value?.statuses?.length,\n    hasMessages: !!value?.messages,\n    messagesLength: value?.messages?.length,\n    messaging_product: value?.messaging_product,\n    metadata: value?.metadata\n  });\n\n  console.log(\"\\nRequest Structure:\");\n  console.log(\"Entry:\", JSON.stringify(entry, null, 2));\n  console.log(\"Changes:\", JSON.stringify(changes, null, 2));\n  console.log(\"Value:\", JSON.stringify(value, null, 2));\n\n  // Log specific updates\n  const statusUpdates = value?.statuses || [];\n  const messageUpdates = value?.messages || [];\n\n  console.log(\"\\nFound Updates:\");\n  console.log(`Status Updates Count: ${statusUpdates.length}`);\n  console.log(`Message Updates Count: ${messageUpdates.length}`);\n\n  try {\n    console.log('Full webhook payload:', JSON.stringify(req.body, null, 2));\n\n    const value = req.body?.entry?.[0]?.changes?.[0]?.value;\n\n    if (!value) {\n      console.log('No value object in webhook payload');\n      return res.status(200).send('No updates to process');\n    }\n\n    const promises = [];\n\n    // Handle statuses array\n    if (value.statuses && Array.isArray(value.statuses)) {\n      console.log(`\\n=== Processing ${value.statuses.length} Status Updates ===`);\n      console.log('Full value object:', JSON.stringify(value, null, 2));\n\n      for (const status of value.statuses) {\n        console.log('\\nProcessing Status Update:');\n        console.log('Status ID:', status.id);\n        console.log('Status:', status.status);\n        console.log('Recipient:', status.recipient_id);\n        console.log('Timestamp:', status.timestamp);\n        promises.push(insertStatusToDb({\n          messageId: status.id,\n          recipientId: status.recipient_id || value.metadata?.display_phone_number,\n          status: status.status,\n          timestamp: status.timestamp\n        }));\n      }\n    }\n    // Handle direct status in value object\n    else if (value.status && value.id) {\n      console.log('Found direct status update:', {\n        id: value.id,\n        status: value.status,\n        recipient: value.recipient_id || value.metadata?.recipient_id\n      });\n\n      promises.push(insertStatusToDb({\n        messageId: value.id,\n        recipientId: value.recipient_id || value.metadata?.recipient_id,\n        status: value.status,\n        timestamp: req.body?.entry?.[0]?.changes?.[0]?.timestamp\n      }));\n    }\n\n    // Handle message updates\n    if (value.messages && Array.isArray(value.messages)) {\n      console.log(`Processing ${value.messages.length} message updates`);\n\n      for (const msg of value.messages) {\n        promises.push(insertStatusToDb({\n          messageId: msg.id,\n          recipientId: msg.from,\n          status: 'received',\n          timestamp: Math.floor(Date.now() / 1000)\n        }));\n      }\n    }\n\n\n    if (promises.length === 0) {\n      console.log('No status updates to process');\n      return res.status(200).send('No status updates found');\n    }\n\n    await Promise.all(promises);\n    console.log(`Successfully processed ${promises.length} status updates`);\n    return res.status(200).send('Status updates processed successfully');\n  } catch (error) {\n    console.error('Error processing webhook:', error);\n    return res.status(500).send('Error processing webhook'); //Improved error response\n  }\n});\n\nmodule.exports = router;"}]}"},{"path":"public/billing.html","content":"<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
